    var dates = $("#date-khoi-hanh").text();
    var n = dates.search("Chủ nhật");
    var cn = dates.substring(n, 8);
    var numb = dates.match(/\d/g);
    if (n > -1) {
        if (numb && numb.length) {
            numb.push('1');
        } else {
            numb = ['1'];
        }
    }
    var dateToday = new Date();
    function DisableMonday(date) {
        var day = date.getDay();
        var i;
        if (numb && numb.length) {
            for (i = 0; i < numb.length; i++) {
                var m = numb[i] - 1;
                var m1 = numb[i + 1] - 1;
                var m2 = numb[i + 2] - 1;
                var m3 = numb[i + 3] - 1;
                var m4 = numb[i + 4] - 1;
                var m5 = numb[i + 5] - 1;
                var m6 = numb[i + 6] - 1;
                if (day == m || day == m1 || day == m2 || day == m3 || day == m4 || day == m5 || day == m6) {
                    return [true];
                } else {
                    return [false];
                }
            }
        } else {
            return [true];
        }
    }
    $(".tourmaster-datepicker").datepicker({
        defaultDate: "",
        changeMonth: true,
        changeYear: true,
        numberOfMonths: 1,
        minDate: dateToday,
        beforeShowDay: DisableMonday
    });
});
</script>